import AppLayout from "../components/AppLayout";

const Home = () => {
    const { isLoggedIn } = useSelector((state) => state.user);
    const { mainPosts } = useSelectpr((state) => state.post);
    return (
        <AppLayout>
            {isLoggedIn && <PostForm />}
            {mainPosts.map((post, index) => <PostCard post={post}/>)}
        </AppLayout>
    );
}

export default Home;
