import { Avatar, Card, Button } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useCallback } from 'react';
import { logoutRequestAction } from '../../reducers/user';
import { array } from 'prop-types';

const UserProfile = () => {
    const dispatch = useDispatch();
    const { me, logOutLoading } = useSelector((state) => state.user);
    const onLogOut = useCallback(() => {
        dispatch(logoutRequestAction());
    });
    return(
        <>
            <Card
                actions={[
                    <div key="writeNum">적은 글 수 <br />{me.data.postListDto}</div>,
                    <div key="following">팔로잉<br />{findFollowing(me.data.preContentInfoDtoList)}</div>,
                    <div key="following">팔로워<br />{findFollower(me.data.preContentInfoDtoList)}</div>
                ]}
            >
                <Card.Meta
                    avatar={<Avatar>{me?.data.nickname}</Avatar>}
                    title={me?.data.nickname}
                />
                <Button onClick={onLogOut} loading={logOutLoading}>로그아웃</Button>
            </Card>
        </>
    );
}

const findPosts = function (arrayData){
    let postsData = arrayData.length;
    return postsData;
}

const findFollowing = function (arrayData){
    let followingData = arrayData.filter(element => element.contentType === 'FOLLOWING');
    return followingData.length;
}

const findFollower = function (arrayData){
    let followerData = arrayData.filter(element => element.contentType === 'FOLLOWED');
    return followerData.length;
}

export default UserProfile;
