/** @jsxImportSource @emotion/react */
import { Globla, jsx, css } from '@emotion/react'
import {func } from "prop-types"
import { useState } from "react";
import Slick from 'react-slick';
import PropTypes from 'prop-types';

const slickStyle = css`
    position: fixed;
    z-index: 5000;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
`;

const slickWrapperStyle = css`
    height: calc(100% - 44px);
    background: #090909;
`;

const headerStyle = css`
    header: 44px;
    background: white;
    position: relative;
    padding: 0;
    text-align:center;

    & h1 {
        margin: 0;
        font-size: 17px;
        color: #333;
        line-height: 44px;
    }

    & button {
        position: absolute;
        right: 0;
        top: 0;
        padding: 15px;
        line-height: 14px;
        cursor: pointer;
    }
` ;

const imgStyle = css`
    padding: 32px;
    text-align: center;

    & img {
        margin: 0 auto;
        max-height: 750px;
    }
`;

const indicatorStyle = css`
    text-align: center;

    & > div {
        width: 75;
        height: 30px;
        line-height: 30px;
        border-radius: 15px;
        background: #313131;
        display: inline-block;
        text-align: center;
        color: white;
        font-size: 15px;
    }
`

const globalStyle = css`
    *{
        .slick-slide{
            display: inline-block;
        }
    }
`

const ImagesZoom = ({ images, onClose}) => {
    const [currentState, setCurrentSlide] = useState(0);
    return (
        <div>
            <header css={headerStyle}>
                <h1>상세 이미지</h1>
                <button onClick={onClose}>X</button>
            </header>
            <div>
                <div css={slickWrapperStyle}>
                    <Slick css={slickStyle}
                        initialSlide={0}
                        afterChange={(slide) => setCurrentSlide(slide)}
                        infinite
                        arrows={false}
                        slidesToShow={1}
                        slidesToScroll={1}
                    >
                        {images.map((v) => {
                            <div css={imgStyle} key={v.src}>
                                <img src={v.src} altt={v.src} />  
                            </div>
                        })}
                    </Slick>
                </div>
            </div>
        </div>
    );
}

ImagesZoom.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object).isRequired,
    onClose: PropTypes.func.isRequired,
}

export default ImagesZoom;
