// ES6 Modules or TypeScript
import Swal from 'sweetalert2'

const MySwal = withReactContent(Swal);

export const warningMsg = function(text) {
    MySwal.fire({
        title: <strong>Error!</strong>,
        html: <i>${text}</i>,
        icon: 'warning'
    })
}
