export const initialState = {
    isLoggingIn: false, // 로그인 시도중
    isLoggedIn: false,
    isLoggingOut: false, // 로그아웃 시도중
    me: null,
    signUpData: {},
    loginData: {},
} 

export const loginRequestAction = (data) => {
    console.log("loginRequestAction start!");
    return {
        type: 'LOG_IN_REQUEST',
        data,
    }
}
export const logoutRequestAction = (data) => {
    console.log('logout request action in reducers');
    return {
        type: 'LOG_OUT_REQUEST',
    }
}
export const logoutSuccessAction = (data) => {
    console.log("log out success in rdeuders funcd")
    console.log(data);
    return {
        type: 'LOG_OUT_SUCCESS',
    }
}
export const logoutFailureAction = (data) => {
    return {
        type: 'LOG_OUT_FAILURE',
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type){
        case 'LOG_IN_REQUEST':
            console.log("reducer login");
            console.log("action.data in reducers" , action.data);
            return{
                ...state,
                isLoggingIn: true,
            };
        case 'LOG_IN_SUCCESS' :
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me: { ...action.data, nickname: 'thelovemsg123'}
            };
        case 'LOG_IN_FAILURE' :
            return{
                ...state,
                isLoggingIn: false,
            };
        case 'LOG_OUT_REQUEST':
            console.log("log out request in reducer");
            return{
                ...state,
                isLoggingOut: true,
            };
        case 'LOG_OUT_SUCCESS':
            console.log("log out success in reducers");
            return{
                ...state,
                isLoggingOut: false,
                isLoggedIn: false,
                me: null,
            };
        case 'LOG_OUT_FAILURE':
            return{
                ...state,
                isLoggingOut: false,
            };
        default:
            return state;
    }
};

export default reducer;
