// ES6 Modules or TypeScript
import Swal from 'sweetalert2';

export const warningMsg = function(text) {
    Swal.fire({
        title: 'Error!',
        text: {text},
        icon: 'error',
        confirmButtonText: 'Ok'
      })
}
