/** @jsxImportSource @emotion/react */
import { jsx, css } from '@emotion/react'
import Head from 'next/head';
import { Form, Input, Button, DatePicker, Space } from 'antd';
import AppLayout from '../components/AppLayout';
import { useCallback, useState } from 'react';
import Link from 'next/link';
import useInput from '../hooks/useInput';

const signupButtonStyle = css`
  margin-top:20px;
`

const Signup = () => {
  const onSubmit = useCallback(() => {

  }, []);

  const [id, onChangeId] = useInput('');
  const [password, onChangePassword] = useInput('');
  const [passwordCheck, setPasswordCheck] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [nickname, onChangeNickname] = useInput('');

  const onChangePasswordCheck = useCallback((e) => {
    setPasswordCheck(e.target.value);
    setPasswordError(e.target.value !== 'password');
  }, [password])

  const changeBirthDay = useCallback((date, dateString) => {
    console.log(date);
    console.log(dateString);
  })
  return (
    <AppLayout>
      <Head>회원가입 | Miniproject</Head>
      <Form onFinish={onSubmit}>
        <div>
          <label htmlFor="user-id">아이디</label>
          <br /> 
          <Input name="user-id" value={id} onChange={onChangeId} />
        </div>
        <div>
          <label htmlFor="user-nickname">닉네임</label> 
          <br /> 
          <Input name="user-nickname" value={nickname} onChange={onChangeNickname} />
        </div>
        <div>
          <label htmlFor="user-password">비밀번호</label>
          <br /> 
          <Input name="user-password" type="password" value={password} onChange={onChangePassword} />
        </div>
        <div>
          <label htmlFor="user-password-check">비밀번호 확인</label>
          <br /> 
          <Input name="user-password-check" type="password" value={passwordCheck} onChange={setPasswordCheck} />
        </div>
        <div>
          <label htmlFor="user-birthday">생일</label>
          <br /> 
          <DatePicker onChange={changeBirthDay}/>
        </div>
        <div>
          <Link href="/signup"><a><Button css={signupButtonStyle}>회원가입</Button></a></Link>
        </div>
      </Form>
    </AppLayout>
  )
}

export default Signup;
