import PropTypes from 'prop-types';

const PostImages = ({ images }) => {
    const [showImagesZoom, setShowImagesZoom] = useState(false);
    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, []);
    if (images.length === 1){
        return (
            <>
                <img role="presentation" width="50%" src={images[0].src} alt={image[0].src} onClick={onZoom} />
            </>
         )
    }
    if (images.length === 2){
        return (
            <>
                <img role="presentation" width="50%" src={images[0].src} alt={image[0].src} onClick={onZoom} />
                <img role="presentation" width="50%" src={images[1].src} alt={image[1].src} onClick={onZoom} />
            </>
         )
    }
    return (
        <>
            <div>
                <img role="presentation" width="50%" src={images[0].src} alt={images[0].src} onClick={onZoom}></img>
                <div
                    role="presentation"
                    style={{}}
                    onClick={onZoom}
                ></div>
            </div>
        </>
    )
};

PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
}

export default PostImages;
