/** @jsxImportSource @emotion/react */
import { jsx, css } from '@emotion/react'
import { EllipsisOutlined, HeartOutlined, HeartTwoTone, MessageOutlined, RetweetOutlined } from '@ant-design/icons';
import PropTypes from 'prop-types';
import { Form, Input, Button, Card, Popover, Avatar, List, Comment } from 'antd';
import ButtonGroup from 'antd/lib/button/button-group';
import { useCallback, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import PostImages from './PostImages';
import CommentForm from './CommentForm';
import PostCardContent from './PostCardContent';
import { REMOVE_POST_REQUEST } from '../../../reducers/post';
import FollowButton from './FollowButton';

const PostCard = ({ post }) => {
    const dispatch = useDispatch();
    const { removePostLoading } = useSelector((state) => state.post);
    const [liked, setLiked] = useState(false);
    const [commentFormOpend, setCommentFormOpened] = useState(false); 
    const onToggleLike = useCallback(() => {
        setLiked((prev) => !prev);
    }, []);
    const onToggleComment = useCallback(() => {
        setCommentFormOpened((prev) => !prev);
    }, []);
    const id = useSelector((state) => state.user.me?.id); // optional chaining
    // const id = me && me.id;

    const onRemovePost = useCallback(() => {
        dispatch({
            type: REMOVE_POST_REQUEST,
            data: post.id
        })
    }, []);
    return (
       <div>
           {console.log(post)}
           <Card key={post.postId}
                cover={post.imageList[0] && <PostImages images={post.imageList} />}
                actions={[
                    <RetweetOutlined key="retweet"/>, 
                    liked
                        ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onToggleLike} />
                        : <HeartOutlined key="heart" onClick={onToggleLike}/>
                    ,
                    <MessageOutlined key="comment" onClick={onToggleComment} />,
                    <Popover key="more" content={(
                        <Button.Group>
                            {id && post.User.id === id ? (
                            <>
                                <Button>수정</Button>
                                <Button type="danger" loading={removePostLoading} onClick={onRemovePost}>삭제</Button>
                            </>
                            ) : <Button>신고</Button>}
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
                extra={ id && <FollowButton post={post} />}
            >
            <Card.Meta
                avatar={<Avatar>{post.writer[0]}</Avatar>}
                title={post.writer} 
                description={<PostCardContent postData={post.description} />}
            />
            </Card>
            {commentFormOpend && (
                <div>
                    {/* 어떤 게시글에 댓글을 달지 정보가 필요 - 게시글 id */}
                    <CommentForm post={post}/>
                    <List 
                        header={`${post.commentList.length}개의 댓글`}
                        itemLayout="horizontal"
                        dataSource={post.commentList}
                        renderItem={(item) => (
                            <li>
                                <Comment
                                    author={item.commentWriter}
                                    avatar={<Avatar>{item.commentWriter[0]}</Avatar>}
                                    content={item.commentDescription}
                                />
                            </li>
                        )}
                    />
                </div>)}
           {/* <CommentForm />
           <Comments /> */}
       </div>
    )
}

PostCard.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.object,
        Comments: PropTypes.arrayOf(PropTypes.object),
        Images: PropTypes.arrayOf(PropTypes.object),
    }).isRequired,
}

export default PostCard;
