import PropTypes from 'prop-types';
import Link from 'next/link';
import { Layout, Menu, Input, Select, Row, Col } from 'antd'; // 필요한 컴포넌트를 가져온다
import { useState } from 'react';
const { Header, Footer, Sider, Content } = Layout;
const { Option } = Select;
const AppLayout = ({ children }) => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    return (
        <Layout>
            <Header>
                <Input.Group compact style={{verticalAlign: 'middle', width:"50%"}}>
                    <Select defaultValue="All">
                        <Option value="All">All</Option>
                        <Option value="Car">Car</Option>
                    </Select>
                    <Input style={{ width: '80%' }} defaultValue="All Test" />
                </Input.Group>
            </Header>
            <Layout>
                <Content> 
                    <Row gutter={8}>
                        <Col xs={24} md={6}>
                            <Menu mode="horizontal">
                                <Menu.Item>
                                    <Link href="/"><a>메인</a></Link>
                                </Menu.Item>
                                <Menu.Item>
                                    <Link href="/signin"><a>로그인</a></Link>
                                </Menu.Item>
                                <Menu.Item>
                                    <Link href="/signup"><a>회원가입</a></Link>
                                </Menu.Item>
                            </Menu>
                        </Col>
                        <Col xs={24} md={12}>
                            Test1123123123123213
                        </Col>
                        <Col xs={24} md={6}>
                            etsetetsetetseet
                        </Col>
                    </Row>
                    {children}
                </Content>
            </Layout>
        </Layout>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
}

export default AppLayout;
