/** @jsxImportSource @emotion/react */
import { jsx, css } from '@emotion/react'
import PropTypes from 'prop-types';
import Link from 'next/link';
import { Layout, Menu, Input, Select, Row, Col } from 'antd'; // 필요한 컴포넌트를 가져온다
import { useState } from 'react';
const { Header, Content } = Layout;
import UserProfile from '../components/UserProfile';
import SitesContent from '../components/SitesContent';
import LoginForm from '../components/LoginForm';
import ChattingForm from './ChattingForm';
import SearchForm from './SearchForm';
import LeftSider from './LeftSider';

const contentStyle = css`
    margin-top:20px;
`

const AppLayout = ({ children }) => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    return (
        <Layout>
            <Header>
                <SearchForm />
            </Header>
            <Layout>
                <Content css={contentStyle}> 
                    <Row gutter={20}> 
                        <Col xs={24} md={6}>
                           <LeftSider/>
                        </Col>
                        <Col xs={24} md={12}>
                            <SitesContent /> 
                        </Col>
                        <Col xs={24} md={6}>
                           {isLoggedIn ? <UserProfile setIsLoggedIn/> : <LoginForm setIsLoggedIn={setIsLoggedIn}/>} 
                        </Col>
                    </Row>
                    {children}
                </Content>
            </Layout>
        </Layout>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
}

export default AppLayout;
