import PropTypes from 'prop-types';
import styled from '@emotion/react';
import Link from 'next/link';
import { Layout, Menu, Input, Select, Row, Col } from 'antd'; // 필요한 컴포넌트를 가져온다
import { useState } from 'react';
const { Header, Content } = Layout;
const { Option } = Select;
import UserProfile from '../components/UserProfile';
import SitesContent from '../components/SitesContent';
import LoginForm from '../components/LoginForm';
import ChattingForm from './ChattingForm';

const AppLayout = ({ children }) => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    return (
        <Layout>
            <Header>
                <Input.Search style={{ textAlign:'center ', verticalAlign:'center' }} defaultValue="" />
            </Header>
            <Layout>
                <Content> 
                    <Row gutter={8}> 
                        <Col xs={24} md={6}>
                            <Menu mode="horizontal">
                                <Menu.Item>
                                    <Link href="/"><a>메인</a></Link>
                                </Menu.Item>
                                <Menu.Item>
                                    <Link href="/signin"><a>로그인</a></Link>
                                </Menu.Item>
                                <Menu.Item>
                                    <Link href="/signup"><a>회원가입</a></Link>
                                </Menu.Item>
                            </Menu>
                        </Col>
                        <Col xs={24} md={12}>
                            {isLoggedIn ? <SitesContent /> : <LoginForm /> }
                        </Col>
                        <Col xs={24} md={6}>
                           {isLoggedIn ? <UserProfile /> : <ChattingForm/>} 
                        </Col>
                    </Row>
                    {children}
                </Content>
            </Layout>
        </Layout>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
}

export default AppLayout;
